xwikidoc.content:
import com.xpn.xwiki.XWikiContext;
import com.xpn.xwiki.doc.XWikiDocument;
import com.xpn.xwiki.objects.BaseObject;
import com.xpn.xwiki.validation.XWikiValidationInterface;
import com.xpn.xwiki.validation.XWikiValidationStatus;

public class Val implements XWikiValidationInterface {
  @Override
  public boolean validateDocument(XWikiDocument doc, XWikiContext context) {
    def service = com.xpn.xwiki.web.Utils.getComponent(org.projectsforge.xwiki.bibliography.service.BibliographyService.class)
    if (!service.validateEntry(doc)) {
      XWikiValidationStatus.addErrorToContext("Bibliography.Code.EntryClass", "", "", "bibliography.entry.val_id",
          context);
      return false;
    } else {
      return true;
    }
  }

  @Override
  public boolean validateObject(BaseObject object, XWikiContext context) {
    return true;
  }
}
